import Header from "../../components/banner/banner";
import "./ToolsPage.css";
import React, { useState } from 'react'
import axios from 'axios'
import WeatherWidget from "../../components/weatherwidget/weatherwidget";


//passing state from child to parent to child
export default function ToolsPage() {

  const [data, setData] = useState({})
  const [location, setLocation] = useState('')

  const url = `https://api.openweathermap.org/data/2.5/weather?q=${location}&units=imperial&appid=c36ffcd6dbd3d32e3deb52499600743c`

  const searchLocation = (event) => {
    if (event.key === 'Enter') {
      axios.get(url).then((response) => {
        setData(response.data)
        console.log(response.data)
      })
      setLocation('')
    }
  }

  return (        
    <>
      <Header />
      <div className="home">

          <div className="container pb-5" style={{ width: "25rem" }}>
            <h1 className="font-weight-bold header-animated py-4">Weather App</h1>
            <div className="align-items-center justify-content-center">
              <WeatherWidget data={data}/>
            </div>
          </div>
      </div>
    </>
  )
}
