import Header from "../../components/banner/banner";
import "./ToolsPage.css";
import React, { useState } from 'react'
import axios from 'axios'


//passing state from child to parent to child
export default function ToolsPage() {

  const [data, setData] = useState({})
  const [location, setLocation] = useState('')

  const url = `https://api.openweathermap.org/data/2.5/weather?q=${location}&units=imperial&appid=c36ffcd6dbd3d32e3deb52499600743c`

  const searchLocation = (event) => {
    if (event.key === 'Enter') {
      axios.get(url).then((response) => {
        setData(response.data)
        console.log(response.data)
      })
      setLocation('')
    }
  }

  return (        
    <>
      <Header />
      <div className="home">

          <div className="container pb-5" style={{ width: "25rem" }}>
            <h1 className="font-weight-bold header-animated py-4">Weather App</h1>
            <div className="align-items-center justify-content-center">
              <WeatherCard weatherData={data} showError={showWeathError} error={weathError} />
            </div>
          </div>

          <div className="app">
          <div className="search">
            <input
              value={location}
              onChange={event => setLocation(event.target.value)}
              onKeyPress={searchLocation}
              placeholder='Enter Location'
              type="text" />
          </div>
          <div className="container">
            <div className="top">
              <div className="location">
                <p>{data.name}</p>
              </div>
              <div className="temp">
                {data.main ? <h1>{data.main.temp.toFixed()}°F</h1> : null}
              </div>
              <div className="description">
                {data.weather ? <p>{data.weather[0].main}</p> : null}
              </div>
            </div>

            {data.name !== undefined &&
              <div className="bottom">
                <div className="feels">
                  {data.main ? <p className='bold'>{data.main.feels_like.toFixed()}°F</p> : null}
                  <p>Feels Like</p>
                </div>
                <div className="humidity">
                  {data.main ? <p className='bold'>{data.main.humidity}%</p> : null}
                  <p>Humidity</p>
                </div>
                <div className="wind">
                  {data.wind ? <p className='bold'>{data.wind.speed.toFixed()} MPH</p> : null}
                  <p>Wind Speed</p>
                </div>
              </div>
            }
          </div>
          </div>
      </div>
    </>
  )
}
