import { Link } from "react-router-dom";
import "./sidebar.css";
import {subscribeToTopic, unSubscribeFromTopic} from "../socket";
import {updateState,updatePost} from "../../pages/homepage/Homepage";
import React, { useEffect, useState , Component }  from 'react'

function subScribeCallback(msg) {

  var dataObj = JSON.parse(JSON.parse(msg.body).data);
  console.log(dataObj);
  updatePost(dataObj);
}


class Sidebar extends Component {
  constructor(props) {
    super(props)
  }


  render() {

    function handleChange(e){
      let isChecked = e.target.checked;

      if(isChecked)
      {
        subscribeToTopic(e.target.id, subScribeCallback);
      }
      else
      {
        unSubscribeFromTopic(e.target.id);
      }
    }
    return (
      <div className="sidebar">
      <div className="sidebarItem">
        <span className="sidebarTitle">CATEGORIES</span>
        <ul className="sidebarList">
          <li className="sidebarListItem">
          <input className = "checkbox"  onChange={handleChange} type="checkbox" id="ai" name="ai" value="ai"/>
            <Link className="link" to="/posts?cat=mlai">
              ML & AI
            </Link>

          </li>
          <li className="sidebarListItem">
          <input  className = "checkbox" onChange={handleChange} type="checkbox" id="bigdata" name="bigdata" value="bigdata"/>
            <Link className="link" to="/posts?cat=bigdata">
              Big Data
            </Link>
          </li>
          <li className="sidebarListItem">
          <input  className = "checkbox" onChange={handleChange} type="checkbox" id="micro" name="micro" value="micro"/>
            <Link className="link" to="/posts?cat=micro">
              Micro Services
            </Link>
          </li>
        </ul>
      </div>

    </div>
    )
  }
}  

export default Sidebar;


